const list = items.map((item, index) => {
    text += `${index + 1}. ${item.json.fecha} ${item.json.hora} - ${item.json.nombre}\n`;
    return { id_cita: item.json.id_cita, detail: `${item.json.fecha} ${item.json.hora} - ${item.json.nombre}` };
});

text += "\nIngresa el n√∫mero de la cita.\n9. Cancelar";

